import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import Table from 'react-bootstrap/Table';
// import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';
// import { Link } from 'react-router-dom';
import { useLogout } from '../hooks/useLogout'

const UserHome = () => {
    const {user} = useAuthContext()
    const [meetings, setMeetings] = useState(null)
    const [selectMeet,setSelectMeet]= useState(null)
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    // const { user } = useAuthContext()
    const {logout} = useLogout()

    const handleClick = () =>{
      logout()
    }

    const handleSelect=(id)=>{
        setSelectMeet(meetings.filter(m=>m.meet_id===id))
        handleShow()
        console.log(selectMeet)
    }

    useEffect(()=>{
        const fetchMeetings = async () => {
            const response = await fetch('/userhome',{
              headers:{
                'Authorization':`Bearer ${user.token}`
              }
            })
      
            const json = await response.json()
      
            if(response.ok){
              setMeetings(json)
            }
          }
        
      
          if (user) {
            fetchMeetings()
          }
    },[user])
    return ( 
        <>
        <div className='container mt-5'>

        <h1>Meeting App</h1>            <Button onClick={handleClick} className='mt-2'>Logout</Button>
        
        <div className='mt-5'>

        <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>Meet_ID</th>
            <th>Agenda</th>
            <th>Topic</th>
            <th>Draft</th>
            <th>View Details</th>
          </tr>
        </thead>
        <tbody>
        {meetings && meetings.map(m=>
            <tr key={m.meet_id}>
                <td>{m.meet_id}</td>
                <td>{m.agenda}</td>
                <td>{m.topic}</td>
                <td>{m.draft}</td>
                <td><button className='btn' onClick={()=>handleSelect(m.meet_id)}>Check here</button></td>

          </tr>
          )}
        </tbody>
      </Table>
        </div>
    </div>


    <Modal show={show} onHide={handleClose}>
    <Modal.Header closeButton>
      <Modal.Title>Modal heading</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <Form>
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="name@example.com"
            autoFocus
          />
        </Form.Group>
        <Form.Group
          className="mb-3"
          controlId="exampleForm.ControlTextarea1"
        >
          <Form.Label>Example textarea</Form.Label>
          <Form.Control as="textarea" rows={3} />
        </Form.Group>
      </Form>
    </Modal.Body>
    <Modal.Footer>
      <Button variant="secondary" onClick={handleClose}>
        Close
      </Button>
      <Button variant="primary" onClick={handleClose}>
        Save Changes
      </Button>
    </Modal.Footer>
  </Modal>
        </>
 
     );
}
 
export default UserHome;
